import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, SafeAreaView, ImageBackground, TouchableOpacity, TextInput } from 'react-native';

const API_KEY = 'f6333f36905ea5207665d473b24a65c1'; // Your API Key

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [city, setCity] = useState('Islamabad');

  useEffect(() => {
    fetchWeatherData(city);
  }, []);

  const fetchWeatherData = async (cityName) => {
    try {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`;
      console.log('Fetching data from:', url);
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error(`HTTP Error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Fetched Data:', data);

      if (data.cod !== 200) {
        setError(data.message || 'Failed to fetch weather data');
        setWeatherData(null);
      } else {
        setWeatherData(data);
        setError(null);
      }
    } catch (err) {
      setError('Unable to fetch weather data. Please check your API key and internet connection.');
      console.error('Fetch Error:', err);
    }
  };

  const getBackgroundImage = (weatherCondition) => {
    if (!weatherCondition) return 'data:image/jpeg;base64,';
    
    const lowerCondition = weatherCondition.toLowerCase();
    if (lowerCondition.includes('clear')) return 'data:image/jpeg;base64,';
    if (lowerCondition.includes('cloud')) return 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLiEuCv-0iIh7amA8k9nsYIYoiUe5QRyP1ig&s';
    if (lowerCondition.includes('rain')) return 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1hUfBrtKtxFz11qrrfrGJKFURaPQmeghnXg&s';
    if (lowerCondition.includes('snow')) return 'data:image/jpeg;base64,';
    return 'data:image/jpeg;base64,';
  };

  return (
    <SafeAreaView style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Enter city name"
        placeholderTextColor="#ccc"
        value={city}
        onChangeText={setCity}
      />
      <TouchableOpacity style={styles.searchButton} onPress={() => fetchWeatherData(city)}>
        <Text style={styles.searchButtonText}>Search</Text>
      </TouchableOpacity>
      {weatherData ? (
        <ImageBackground
          source={{ uri: getBackgroundImage(weatherData.weather[0]?.main) }}
          style={styles.backgroundImage}
        >
          <Text style={styles.cityName}>{weatherData.name}</Text>
          <Text style={styles.date}>{new Date().toLocaleDateString()}</Text>
          <Text style={styles.day}>{new Date().toLocaleString('en-US', { weekday: 'long' })}</Text>
          <Text style={styles.temperature}>{weatherData.main.temp}Â°C</Text>
          <Text style={styles.weatherCondition}>{weatherData.weather[0]?.description}</Text>
          <TouchableOpacity style={styles.refreshButton} onPress={() => fetchWeatherData(city)}>
            <Text style={styles.refreshButtonText}>Refresh</Text>
          </TouchableOpacity>
        </ImageBackground>
      ) : (
        <Text style={styles.errorText}>{error || 'Loading weather data...'}</Text>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  backgroundImage: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: '100%',
  },
  cityName: {
    fontSize: 40,
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: 10,
  },
  date: {
    fontSize: 18,
    color: '#fff',
    marginBottom: 5,
  },
  day: {
    fontSize: 18,
    color: '#fff',
    marginBottom: 15,
  },
  temperature: {
    fontSize: 60,
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: 10,
  },
  weatherCondition: {
    fontSize: 20,
    color: '#fff',
    marginBottom: 20,
  },
  refreshButton: {
    paddingVertical: 10,
    paddingHorizontal: 40,
    backgroundColor: '#2196f3',
    borderRadius: 30,
  },
  refreshButtonText: {
    color: '#fff',
    fontSize: 18,
  },
  errorText: {
    fontSize: 18,
    color: 'red',
    textAlign: 'center',
    marginTop: 20,
  },
  input: {
    width: '80%',
    padding: 10,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    marginBottom: 10,
    color: '#000',
    backgroundColor: '#fff',
    textAlign: 'center',
  },
  searchButton: {
    paddingVertical: 10,
    paddingHorizontal: 40,
    backgroundColor: '#4CAF50',
    borderRadius: 30,
    marginBottom: 20,
  },
  searchButtonText: {
    color: '#fff',
    fontSize: 18,
  },
});

export default WeatherApp;